import { motion } from "motion/react";

function Burger({ isOpen, handleOpenBurgerMenuChanges }) {
  return (
    <>
      <motion.button
        onClick={handleOpenBurgerMenuChanges}
        animate={isOpen ? { opacity: 1 } : { opacity: 0.5 }}
      >
        <div className="w-[2rem] h-[2rem] grid gap-[5px] [&>*]:bg-opposite-color">
          <motion.div className="w-[2rem] h-[0.5rem] rounded-[10px]" animate={isOpen ? { opacity: 1 } : { opacity: 0.5 }}></motion.div>
          <motion.div className="w-[2rem] h-[0.5rem] rounded-[10px]" animate={isOpen ? { opacity: 1 } : { opacity: 0.5 }}></motion.div>
          <motion.div className="w-[2rem] h-[0.5rem] rounded-[10px]" animate={isOpen ? { opacity: 1 } : { opacity: 0.5 }}></motion.div>
        </div>
      </motion.button>
    </>
  );
}

export default Burger;
