import { motion } from "motion/react";

function Burger({ isOpen, handleOpenBurgerMenuChanges }) {
  return (
    <>
      <motion.button onClick={handleOpenBurgerMenuChanges}>
        <div className="w-[3rem] h-[2rem] grid gap-[5px] [&>*]:bg-opposite-color">
          <motion.div
            className="w-[3rem] h-[0.2rem] rounded-[10px]"
            animate={isOpen ? { rotate: 45, y: 10 } : { rotate: 0, y: 0 }}
          ></motion.div>
          <motion.div
            className="w-[3rem] h-[0.2rem] rounded-[10px]"
            animate={isOpen ? { opacity: 0 } : { opacity: 1 }}
          ></motion.div>
          <motion.div
            className="w-[3rem] h-[0.2rem] rounded-[10px]"
            animate={isOpen ? { rotate: -45, y: -10 } : { rotate: 0, y: 0 }}
          ></motion.div>
        </div>
      </motion.button>
    </>
  );
}

export default Burger;
