import { motion } from "motion/react";
import { useState } from "react";

function BurgerMenu({ isOpen }) {
  const [isTutorialOpen, setIsTutorialOpen] = useState(false);

  return (
    <motion.div
      className="fixed top-0 right-0 w-[70%] h-screen bg-white z-[100] shadow-lg [&>*]:text-black flex flex-col justify-center"
      initial={{ x: "100%" }}
      animate={{ x: isOpen ? 0 : "100%" }}
      transition={{ duration: 0.3, ease: "easeInOut" }}
    >
      <div className="flex flex-col justify-end flex-grow">
        {/* Tutorial content - positioned to expand upward */}
        <motion.div
          className="overflow-hidden"
          initial={{ height: 0 }}
          animate={{ height: isTutorialOpen ? "auto" : 0 }}
          transition={{ duration: 0.3, ease: "easeInOut" }}
        >
          <motion.div
            className="p-1"
            initial={{ opacity: 0, y: 20 }}
            animate={{ 
              opacity: isTutorialOpen ? 1 : 0,
              y: isTutorialOpen ? 0 : 20
            }}
            transition={{ duration: 0.3, ease: "easeInOut" }}
          >
            <ol className="mb-2">
              <li>Write your Sentence in the Text Input</li>
              <li>Provide the Generate Amount</li>
              <li>Press Generate!</li>
            </ol>
            <p>Write "{"{}"}" to define a Variable in the Text Input.</p>
          </motion.div>
        </motion.div>
        
        {/* How to Use button */}
        <div className="w-full h-auto">
          <motion.button
            className="w-full h-auto py-2"
            onClick={() => setIsTutorialOpen(!isTutorialOpen)}
          >
            <h4>How to Use</h4>
          </motion.button>
        </div>
      </div>
      
      {/* Dark Mode div stays in place */}
      <div className="w-full h-auto py-2">
        <div className="text-center">Dark Mode</div>
      </div>
    </motion.div>
  );
}

export default BurgerMenu;
