import { useEffect, useState } from "react";

function App() {
  const [isDarkMode, setIsDarkMode] = useState(false);

  const handleDarkModeChanges = () => {
    setIsDarkMode(prev => !prev);
  };

  useEffect(() => {
    if (isDarkMode) {
      document.documentElement.classList.add("dark");
    } else {
      document.documentElement.classList.remove("dark");
    }
  }, [isDarkMode]);

  return (
    <div className="min-h-screen bg-white dark:bg-gray-900 text-black dark:text-white p-8">
      <h1 className="text-2xl mb-4">Dark Mode Test</h1>
      
      <button 
        onClick={handleDarkModeChanges}
        className="px-4 py-2 bg-blue-500 text-white rounded mb-4"
      >
        Toggle Dark Mode
      </button>
      
      <div className="p-4 bg-gray-100 dark:bg-gray-800 text-black dark:text-white mb-4">
        This should change colors: {isDarkMode ? 'DARK' : 'LIGHT'}
      </div>
      
      <div className="p-4 bg-red-50 dark:bg-red-900 text-red-800 dark:text-red-200">
        Another test div
      </div>
    </div>
  );
}

export default App;
