import { motion } from "motion/react";
import { useState } from "react";

function BurgerMenu({ isOpen }) {
  const [isTutorialOpen, setIsTutorialOpen] = useState(false);

  return (
    <motion.div
      className="fixed top-0 right-0 w-[70%] h-screen bg-white z-[100] shadow-lg [&>*]:text-black flex flex-col justify-center gap-[10px]"
      initial={{ x: "100%" }}
      animate={{ x: isOpen ? 0 : "100%" }}
      transition={{ duration: 0.3, ease: "easeInOut" }}
    >
      <motion.div
        className="w-full h-auto"
        animate={{
          y: isTutorialOpen ? -150 : 0, // Adjust this value based on content height
        }}
        transition={{ duration: 0.3, ease: "easeInOut" }}
      >
        <motion.button
          className="w-full h-auto"
          onClick={() => setIsTutorialOpen(!isTutorialOpen)}
        >
          <h4>How to Use</h4>
        </motion.button>
        <motion.div
          className={isTutorialOpen ? "h-auto w-full p-1" : "hidden"}
          initial={{ opacity: 0 }}
          animate={{
            opacity: isTutorialOpen ? 1 : 0,
          }}
          transition={{ duration: 0.3, ease: "easeInOut" }}
        >
          <ol>
            <li>Write your Sentence in the Text Input</li>
            <li>Provide the Generate Amount</li>
            <li>Press Generate!</li>
          </ol>
          <p>Write "{"{}"}" to define a Variable in the Text Input.</p>
        </motion.div>
      </motion.div>
      
      <div className="w-full h-auto absolute bottom-1/2 transform translate-y-1/2">Dark Mode</div>
    </motion.div>
  );
}

export default BurgerMenu;
