import { motion } from "motion/react";

function BurgerMenu({ isOpen }) {
  return (
    <motion.div
      className="fixed top-0 right-0 w-[70%] h-screen bg-white z-[100] shadow-lg [&>*]:text-black grid"
      initial={{ x: "100%" }} 
      animate={{ x: isOpen ? 0 : "100%" }} 
      transition={{ duration: 0.3, ease: "easeInOut" }}
    >
        <div>How to Use</div>
        <div>Dark Mode</div>
    </motion.div>
  );
}

export default BurgerMenu;
