import { useState, useRef, useEffect } from "react";
import { motion } from "motion/react";

export default function BurgerMenu({ isOpen }) {
  const [isTutorialOpen, setIsTutorialOpen] = useState(false);
  const innerRef = useRef(null);
  const [contentHeight, setContentHeight] = useState(0);

  // measure content height (and re-measure on resize)
  useEffect(() => {
    const measure = () => {
      if (innerRef.current) setContentHeight(innerRef.current.scrollHeight);
    };
    measure();
    window.addEventListener("resize", measure);
    return () => window.removeEventListener("resize", measure);
  }, []);

  // ensure we measure after the content is rendered when opening
  useEffect(() => {
    if (!innerRef.current) return;
    if (isTutorialOpen) {
      // measure on next frame after DOM paint
      requestAnimationFrame(() => {
        setContentHeight(innerRef.current.scrollHeight);
      });
    } else {
      // still keep measurement up-to-date
      setContentHeight(innerRef.current.scrollHeight);
    }
  }, [isTutorialOpen]);

  return (
    <motion.div
      className="fixed top-0 right-0 w-[70%] h-screen bg-white z-[100] shadow-lg text-black flex flex-col justify-end p-4"
      initial={{ x: "100%" }}
      animate={{ x: isOpen ? 0 : "100%" }}
      transition={{ duration: 0.3, ease: "easeInOut" }}
    >
      {/* stays put */}
      <div className="w-full">Dark Mode</div>

      {/* HOW TO block (last child so it's anchored to bottom by parent flex) */}
      <div className="w-full relative flex flex-col justify-end">
        {/* content: absolute bottom so it expands upward without moving siblings */}
        <motion.div
          className="absolute left-0 right-0 bottom-0 overflow-hidden"
          animate={{
            height: isTutorialOpen ? contentHeight : 0,
            opacity: isTutorialOpen ? 1 : 0,
          }}
          transition={{ duration: 0.28, ease: "easeInOut" }}
        >
          <div ref={innerRef} className="p-2">
            <ol className="list-decimal list-inside">
              <li>Write your Sentence in the Text Input</li>
              <li>Provide the Generate Amount</li>
              <li>Press Generate!</li>
            </ol>
            <p>Write {"{}"} to define a Variable in the Text Input.</p>
          </div>
        </motion.div>

        {/* button: translate up by contentHeight when open */}
        <motion.button
          className="w-full text-left relative z-20"
          onClick={() => setIsTutorialOpen((s) => !s)}
          animate={{ y: isTutorialOpen ? -contentHeight : 0 }}
          transition={{ duration: 0.28, ease: "easeInOut" }}
        >
          <h4>How to Use</h4>
        </motion.button>
      </div>
    </motion.div>
  );
}
