import { motion } from "motion/react";

function BurgerMenu({ isOpen }) {
  return (
    <>
      <motion.div
        className="absolute w-full h-full bg-white"
        animate={isOpen ? { opacity: 1 } : { opacity: 0 }}
      ></motion.div>
    </>
  );
}

export default BurgerMenu;
