import React, { useState, useEffect } from 'react';

function DarkModeTest() {
  const [isDarkMode, setIsDarkMode] = useState(false);

  const toggleDarkMode = () => {
    setIsDarkMode(prev => {
      const newMode = !prev;
      
      // Apply to document
      if (newMode) {
        document.documentElement.classList.add('dark');
      } else {
        document.documentElement.classList.remove('dark');
      }
      
      console.log('Dark mode:', newMode);
      return newMode;
    });
  };

  // Initial setup
  useEffect(() => {
    if (isDarkMode) {
      document.documentElement.classList.add('dark');
    } else {
      document.documentElement.classList.remove('dark');
    }
  }, []);

  return (
    <div className="min-h-screen p-8 bg-white dark:bg-gray-900 text-black dark:text-white transition-colors duration-300">
      <div className="max-w-2xl mx-auto space-y-6">
        <h1 className="text-3xl font-bold text-gray-900 dark:text-white">
          Dark Mode Test
        </h1>
        
        <button
          onClick={toggleDarkMode}
          className="px-6 py-3 bg-blue-500 hover:bg-blue-600 dark:bg-blue-600 dark:hover:bg-blue-700 text-white rounded-lg transition-colors"
        >
          Toggle Dark Mode (Currently: {isDarkMode ? 'Dark' : 'Light'})
        </button>

        <div className="space-y-4">
          <div className="p-4 bg-gray-100 dark:bg-gray-800 rounded-lg">
            <h2 className="text-lg font-semibold text-gray-800 dark:text-gray-200">
              Test Card 1
            </h2>
            <p className="text-gray-600 dark:text-gray-400">
              This should change from light gray to dark gray background.
            </p>
          </div>

          <div className="p-4 bg-white dark:bg-black border border-gray-300 dark:border-gray-700 rounded-lg">
            <h2 className="text-lg font-semibold text-black dark:text-white">
              Test Card 2
            </h2>
            <p className="text-gray-700 dark:text-gray-300">
              This should change from white to black background.
            </p>
          </div>

          <div className="p-4 bg-red-50 dark:bg-red-900 text-red-800 dark:text-red-200 rounded-lg">
            <h2 className="text-lg font-semibold">
              Colored Test Card
            </h2>
            <p>
              This tests colored backgrounds in dark mode.
            </p>
          </div>
        </div>

        <div className="mt-8 p-4 bg-yellow-50 dark:bg-yellow-900 rounded-lg">
          <h3 className="font-semibold text-yellow-800 dark:text-yellow-200">
            Debug Info:
          </h3>
          <p className="text-sm text-yellow-700 dark:text-yellow-300 mt-2">
            HTML classes: {document.documentElement.className || 'none'}
          </p>
          <p className="text-sm text-yellow-700 dark:text-yellow-300">
            Current mode: {isDarkMode ? 'Dark' : 'Light'}
          </p>
        </div>
      </div>
    </div>
  );
}

export default DarkModeTest;
