import { motion } from "motion/react";
import { useState } from "react";

function BurgerMenu({ isOpen }) {
  const [isTutorialOpen, setIsTutorialOpen] = useState(false);

  return (
    <motion.div
      className="fixed top-0 right-0 w-[70%] h-screen bg-white z-[100] shadow-lg [&>*]:text-black flex flex-col justify-end gap-3 p-4"
      initial={{ x: "100%" }}
      animate={{ x: isOpen ? 0 : "100%" }}
      transition={{ duration: 0.3, ease: "easeInOut" }}
    >
      {/* Other menu items (these stay above and won't be pushed by HowTo) */}
      <div className="w-full">Dark Mode</div>
      <div className="w-full">Other item</div>

      {/* HOW TO USE block (put LAST so it's anchored to bottom) */}
      <div className="w-full">
        <motion.button
          className="w-full text-left"
          onClick={() => setIsTutorialOpen((s) => !s)}
        >
          <h4>How to Use</h4>
        </motion.button>

        {/* animate height so it stays in flow and pushes the button upward */}
        <motion.div
          initial={false}
          animate={{
            height: isTutorialOpen ? "auto" : 0,
            opacity: isTutorialOpen ? 1 : 0,
          }}
          transition={{ duration: 0.28, ease: "easeInOut" }}
          className="overflow-hidden"
        >
          <div className="p-2">
            <ol className="list-decimal list-inside">
              <li>Write your Sentence in the Text Input</li>
              <li>Provide the Generate Amount</li>
              <li>Press Generate!</li>
            </ol>
            <p>Write {"{}"} to define a Variable in the Text Input.</p>
          </div>
        </motion.div>
      </div>
    </motion.div>
  );
}

export default BurgerMenu;
