import { useState } from "react";
import VarTableRowData from "./var-table-row-data";

function VariableModal({
  modalState,
  setModalState,
  incomingIndex,
  incomingValues,
  incomingHandleVariablesChanges,
}) {
  function changeModalCondition() {
    setModalState(false); // Also fix this - use false instead of !modalState
  }

  if (!incomingValues) {
    return null;
  }

  return (
    <>
      {modalState ? (
        <div className="w-screen h-screen inset-0 fixed z-[100]">
          <div className="w-screen h-screen inset-0 fixed bg-[rgba(49,49,49,0.8)]">
            <div className="absolute -translate-x-2/4 -translate-y-2/4 leading-[1.4] max-w-[600px] min-w-[300px] px-7 py-3.5 rounded-[3px] left-2/4 top-2/4 bg-main-color">
              <div>
                {incomingValues.name}
                {/* Test with a simple input first */}
                <input 
                  type="text" 
                  value={incomingValues.name || ''} 
                  onChange={(e) => incomingHandleVariablesChanges(incomingIndex, 'name', e.target.value)}
                  className="w-full text-center bg-transparent text-white placeholder-sub-color"
                />
              </div>

              <button
                onClick={changeModalCondition}
                className="bg-opposite-color border-2 border-main-color p-[10px] hover:bg-main-color hover:border-opposite-color"
              >
                Close button
              </button>
            </div>
          </div>
        </div>
      ) : null}
    </>
  );
}

export default VariableModal;
