import { motion } from "motion/react";
import { useState } from "react";

function BurgerMenu({ isOpen }) {
  const [isTutorialOpen, setIsTutorialOpen] = useState(false);

  return (
    <motion.div
      className="fixed top-0 right-0 w-[70%] h-screen bg-white z-[100] shadow-lg [&>*]:text-black flex flex-col justify-center gap-[10px]"
      initial={{ x: "100%" }}
      animate={{ x: isOpen ? 0 : "100%" }}
      transition={{ duration: 0.3, ease: "easeInOut" }}
    >
      <div className="w-full h-auto">
        <button
          className="w-full h-auto"
          onClick={() => setIsTutorialOpen(!isTutorialOpen)}
        >
          <h3>How to Use</h3>
        </button>
        <motion.div
          className={isTutorialOpen ? "" : "hidden"}
          initial={{ y: "100%" }}
          animate={{ y: isTutorialOpen ? 0 : "100%" ,opacity: isTutorialOpen ? 0 : "100%" }}
          transition={{ duration: 0.3, ease: "easeInOut" }}
        >
          <p>
            Write your Sentence in the Text Input, Provide the Generate Amount,
            Press Generate! Write '{}' to define a Variable in the Text Input.
          </p>
        </motion.div>
      </div>
      <div className="w-full h-auto">Dark Mode</div>
    </motion.div>
  );
}

export default BurgerMenu;
